var app_name = "distanceGeofenceCar1";

var duration = flow.get("awtrix_duration");
var lifetime = flow.get("awtrix_lifetime");
var repeat = flow.get("awtrix_repeat");
var prefix = flow.get("awtrix_prefix");
var app_prefix = flow.get("awtrix_app_prefix");

msg.topic = prefix + "/custom/" + app_prefix + app_name;

var home_latitude = flow.get("home_latitude");
var home_longitude = flow.get("home_longitude");
var car_latitude = flow.get("latitude_car1");
var car_longitude = flow.get("longitude_car1");
var geofence = flow.get("geofence_car1");
var icon = "23172";

// --------------------------------------------------
// Dinstance calculation taken from:
// Jörg Hofmann / meintechblog.de
// https://meintechblog.de/2024/06/11/dafuer-nutze-ich-mqtt-und-nodered-im-smarthome/
// --- begin ---

var RADIUS = 6371;

var toRad = function(n) {
  return n * Math.PI / 180;
};

var getDistance = function(from, to) {
  var fromLat = from[0];
  var fromLon = from[1];
  var toLat = to[0];
  var toLon = to[1];
  var dLat = toRad(toLat - fromLat);
  var dLon = toRad(toLon - fromLon);
  fromLat = toRad(fromLat);
  toLat = toRad(toLat);
  var a = Math.pow(Math.sin(dLat / 2), 2) + (Math.pow(Math.sin(dLon / 2), 2) * Math.cos(fromLat) * Math.cos(toLat));
  var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return RADIUS * c;
};

var measurePath = function(points) {
  return points.reduce(function(memo, point) {
    var distance = memo.lastPoint === null ? 0 : getDistance(memo.lastPoint, point);
    return { lastPoint: point, distance: distance + memo.distance };
  }, { lastPoint: null, distance: 0 }).distance;
};

var calcDistance = function(fromLat, fromLon, toLat, toLon) {
  if(typeof fromLat === 'number'){
    return getDistance([fromLat, fromLon], [toLat, toLon]);
  } else {
    return measurePath(fromLat);
  }
};

var value = Number(calcDistance(
  home_latitude,
  home_longitude,
  car_latitude,
  car_longitude
));
// --- end ---
// --------------------------------------------------

var unit = "";

if (value >= 1000) {
    value = value/100 - 0.5;
    value = value.toFixed(1);
    unit = "tkm";
} else if(value >= 10) {
    value = value.toFixed(0);
    unit = "km";
} else if(value >= 1) {
    value = value.toFixed(1);
    unit = "km";
} else {
    value = value * 1000;
    value = value.toFixed(0);
    unit = "m";
}

value = value + " " + unit;

if (geofence != "") {
  value = geofence;
}

msg.payload = {
  "text": value,
  "icon": icon,
  "repeat": repeat,
  "lifetime": lifetime,
  "duration": duration,
  "pushIcon": 0
};

node.status({ fill:"green",shape:"ring",text:value });
return msg;

