var awtrix_abri = false; // Automatic brightness
var awtrix_teff = flow.get("awtrix_teff"); // App transition effect
var awtrix_tspeed = flow.get("awtrix_tspeed"); // App transition time
var awtrix_sspeed = flow.get("awtrix_sspeed"); // Scroll speed

