var awtrix_app_dat = flow.get("awtrix_app_dat"); // Show date
var awtrix_app_hum = flow.get("awtrix_app_hum"); // Show humidity (sensor in device)
var awtrix_app_temp = flow.get("awtrix_app_temp"); // Show temperature (sensor in device)
var awtrix_app_bat = flow.get("awtrix_app_bat"); // Show battery soc of device

// --------------------------------------------------

var prefix = flow.get("awtrix_prefix");
msg.topic = prefix + "/settings";

msg.payload = {
    "BRI": awtrix_bri,
    "ABRI": awtrix_abri,
    "TEFF": awtrix_teff,
    "TSPEED": awtrix_tspeed,
    "SSPEED": awtrix_sspeed,
    "TIM": awtrix_app_tim,
    "DAT": awtrix_app_dat,
    "HUM": awtrix_app_hum,
    "TEMP": awtrix_app_temp,
