var update_available = false;
update_available = flow.get("update_available_car1");
var update_version = flow.get("update_version_car1");

var value = display_name;
var icon = "2735";

if (update_available = true) {
    value = "Update available for " + display_name + ": " + version + " -> " + update_version;
}

msg.payload = {
    "text": value,
    "icon": icon, 
    "repeat": repeat,
    "lifetime": lifetime,
    "duration": duration,
    "pushIcon": 0 
};

node.status({ fill: 'green', shape: 'ring', text: value + ' | ' + update_available });

if (update_available == true || update_version != "" ) {
    return msg;
}

